mapper.update:
update board
set title=#{title}, content=#{content},
writer=#{writer}
where bno=#{bno}

mapper.select:
select b.bno, b.title, b.regDate, b.writer, b.viewCnt, ifnull(comm.c, 0) as commCnt from board as b left join (select count(*) as c, bno from comment group by bno) as comm on b.bno = comm.bno
genre = #{listGenre}
order by b.bno desc limit #{startNum}, 10
